document.addEventListener('DOMContentLoaded', function () {
  var calendarEl = document.getElementById('calendar');
  var calendar = new FullCalendar.Calendar(calendarEl, {
    initialView: 'dayGridMonth',
    titleFormat:{
    month: 'long',
    year: 'numeric',
    day: 'numeric',
    weekday: 'short'
  },
      customButtons: {
      myCustomButton: {
        text: 'Add Booking',
        click: function () {
          alert('Add Booking button clicked!');
        }
      }
    },
    headerToolbar: {
      left: 'prev,next today',

      center: 'title',
      right: 'dayGridMonth,timeGridWeek,timeGridDay'
    },
    events: [
      {
        title: 'Trip to Goa',
        start: '2025-06-15',
        end: '2025-06-18'
      },
      {
        title: 'Business Meeting',
        start: '2025-06-20'
      }
    ],
  selectable: true
  });
  calendar.render();
});
